CREATE proc [dbo].[Usp_GetCurrentStudentExamPerformance]--Usp_GetCurrentStudentExamPerformance 5,''
@SessionID int,
@search varchar(50),
@ExamType int,
@studentClass int,
@subject int
as
begin
declare @SessionText varchar(10)=null
IF(ISNULL(@search,'')='')
 set @search=''
Select top 1 @SessionText=Text from DropDown where value=@SessionID and Category='session'

select tu.Name as StudentName,gb.*,convert(decimal(5,2),((marksAcquired*100)/convert(float,TotalMarks))) as Percentage from(
select RegistrationID,SessionName,classname,sum(MarksAcquired) as marksAcquired,sum(TotalMarks) TotalMarks from (
select sep.RegistrationID,SessionName,classname,marksAcquired,se.TotalMarks
from StudentExamPerformance sep
left join SessionExam se on sep.SessionExamID=se.ID 
where SessionName=@SessionText and se.ExamTypeID=case when @ExamType=0 then se.ExamTypeID else @ExamType end
and se.ClassID=case when @studentClass=0 then se.ClassID else @studentClass end
and se.SubjectID=case when @subject=0 then se.SubjectID else @subject end) actualData
group by SessionName,classname,RegistrationID) gb
inner join tbl_user tu on gb.RegistrationID=tu.RegistrationID
where (tu.Name LIKE '%' + @search + '%')

